document.querySelectorAll(".info_cart-item_btn").forEach(e=>{e.addEventListener("click",()=>{const t=e.closest(".info_cart-item"),o=t.querySelector(".info_cart-item_menu");t.classList.contains("open")?(o.style.maxHeight="165px",t.classList.remove("open"),e.textContent="Show more"):(o.style.maxHeight=o.scrollHeight+"px",t.classList.add("open"),e.textContent="Hide")})});